    def activate(self):
        Angle1 = self.dial.value()
        Angle2 = self.dial_2.value()
        Angle3 = self.dial_3.value()
        Angle4 = self.dial_4.value()
        print('Activated',Angle1, Angle2, Angle3, Angle4)
        self.send_data(Angle1, Angle2, Angle3, Angle4)

    def run_process(self):
        delay1 = self.doubleSpinBox_7.value()
        delay2 = self.doubleSpinBox_5.value()
        delay3 = self.doubleSpinBox_6.value()
        delay4 = self.doubleSpinBox_8.value()
        delays = [delay1,delay2,delay3,delay4]
        Angle1 = self.doubleSpinBox.value()
        Angle2 = self.doubleSpinBox_2.value()
        Angle3 = self.doubleSpinBox_3.value()
        Angle4 = self.doubleSpinBox_4.value()
        Wait1 = self.doubleSpinBox_12.value()
        Wait2 = self.doubleSpinBox_11.value()
        Wait3 = self.doubleSpinBox_9.value()
        Wait4 = self.doubleSpinBox_10.value()

        Angles = [Angle1,Angle2,Angle3,Angle4]
        Waits = [Wait1,Wait2,Wait3,Wait4]
        print(delays,Angles,Waits)
        #get the minimum value in the list
        sorted_time = sorted(delays)
        print('Sorted time',sorted_time)
        indices=sorted(range(len(delays)), key=lambda k: delays[k])
        print('Sorted indices',indices)
        angles_list =[zero_angle,zero_angle,zero_angle,zero_angle]
        time.sleep(sorted_time[0])
        angles_list[indices[0]]= Angles[indices[0]]
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_time[1]-sorted_time[0])
        angles_list[indices[1]]= Angles[indices[1]]
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_time[2]-sorted_time[1])
        angles_list[indices[2]]= Angles[indices[2]]
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_time[3]-sorted_time[2])
        angles_list[indices[3]]= Angles[indices[3]]
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)


        sorted_waits = sorted(Waits)
        print('Sorted wait',sorted_waits)
        indices=sorted(range(len(Waits)), key=lambda k: Waits[k])
        print('Sorted indices',indices)
        time.sleep(sorted_waits[0]-sorted_time[0])
        angles_list[indices[0]]= zero_angle
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_waits[1]-sorted_waits[0]-(sorted_time[1]-sorted_time[0]))
        angles_list[indices[1]]= zero_angle
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_waits[2]-sorted_waits[1]-(sorted_time[2]-sorted_time[1]))
        angles_list[indices[2]]= zero_angle
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
        time.sleep(sorted_waits[3]-sorted_waits[2-(sorted_time[3]-sorted_time[2])])
        angles_list[indices[3]]= zero_angle
        self.send_data(angles_list[0],angles_list[1],angles_list[2],angles_list[3])
        print(angles_list)
    
        print('process completed')
        
    def send_data(self,Angle1, Angle2, Angle3, Angle4):
        connect(Angle1, Angle2, Angle3, Angle4)


        
          
